import React, { useEffect } from "react";
import {
  Container,
  Box,
  Text,
  TabList,
  Tab,
  Tabs,
  TabPanel,
  TabPanels,
  Button,
  Heading,
  VStack,
} from "@chakra-ui/react";
import { useHistory } from "react-router-dom";
import Login from "../components/Authentication/Login";
import Signup from "../components/Authentication/Signup";

const HomePage = () => {
  const history = useHistory();

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    if (userInfo) history.push("/chats");
  }, [history]);

  const containerStyles = {
    background: `url('https://images.unsplash.com/photo-1478760329108-5c3ed9d495a0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1974&q=80')`,
    backgroundSize: "cover",
    minHeight: "100vh",
  };

  const buttonStyles = {
    colorScheme: "teal",
    size: "lg",
    _hover: {
      bg: "teal.500",
    },
  };

  return (
    <Box style={containerStyles}>
      <Container maxW="xl" centerContent>
        <Box
          d="flex"
          justifyContent="center"
          p={5}
          bg="rgba(255, 255, 255, 0.8)"
          w="100%"
          borderRadius="lg"
          boxShadow="lg"
          color="teal.800"
          textAlign="center"
        >
          <VStack spacing={3}>
            <Heading as="h1" fontSize="5xl" fontFamily="Work Sans">
              Let's Talk
            </Heading>
            <Text fontSize="xl" fontWeight="bold">
              Welcome to our chat application
            </Text>
            <Button
              {...buttonStyles}
              onClick={() => history.push("/chats")}
            >
              Get Started
            </Button>
          </VStack>
        </Box>
        <Box
          bg="rgba(255, 255, 255, 0.8)"
          w="100%"
          p={4}
          borderRadius="lg"
          borderWidth="1px"
          mt={5}
        >
          <Tabs isFitted variant="enclosed-colored">
            <TabList justifyContent="center">
              <Tab>Log In</Tab>
              <Tab>Sign Up</Tab>
            </TabList>
            <TabPanels>
              <TabPanel>
                <Login />
              </TabPanel>
              <TabPanel>
                <Signup />
              </TabPanel>
            </TabPanels>
          </Tabs>
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
